import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";

// ========== SAMPLE IMAGES FROM ASSETS ==========
import bhutanImg from "../assets/Package/Id1.png";
import vietnamImg from "../assets/Package/Id2.png";
import dubaiImg from "../assets/Package/Id3.png";
import singaporeImg from "../assets/Package/Id4.png";
import malaysiaImg from "../assets/Package/Id5.png";
import saudiArabiaImg from "../assets/Package/Id6.png";
import europeImg from "../assets/Package/Id7.png";
import ukImg from "../assets/Package/Id8.png";
import uzbekistanImg from "../assets/Package/Id9.png";
import nepalImg from "../assets/Package/Id10.png";
import cambodiaImg from "../assets/Package/Id11.png";


const Holidays = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // ===================== FILTER STATES =====================
  const [category, setCategory] = useState("");
  const [destination, setDestination] = useState("");

  useEffect(() => {
    if (location.state?.filter) {
      setDestination(location.state.filter);
    }
    if (location.state?.category) {
      setCategory(location.state.category);
    } else {
      setCategory("");
    }
  }, [location, location.state]);

  const [nights, setNights] = useState("");
  const [startingCity, setStartingCity] = useState("");
  const [budget, setBudget] = useState([0, 200000]);
  const [flightOption, setFlightOption] = useState("");

  const [isDestOpen, setIsDestOpen] = useState(false);
  const [destSearch, setDestSearch] = useState("");
  const [isStartCityOpen, setIsStartCityOpen] = useState(false);
  const [startCitySearch, setStartCitySearch] = useState("");

  // ===================== PACKAGE DATA =====================
  const [packages, setPackages] = useState([]);
  const [destinationsList, setDestinationsList] = useState([]);
  const [startingCitiesList, setStartingCitiesList] = useState([]);

  useEffect(() => {
    // Fetch packages
    fetch("/api/packages/")
      .then((res) => res.json())
      .then((data) => setPackages(data))
      .catch((err) => console.error("Error fetching packages:", err));

    // Fetch destinations
    fetch("/api/destinations/")
      .then((res) => res.json())
      .then((data) => setDestinationsList(data))
      .catch((err) => console.error("Error fetching destinations:", err));

    // Fetch starting cities
    fetch("/api/starting-cities/")
      .then((res) => res.json())
      .then((data) => setStartingCitiesList(data))
      .catch((err) => console.error("Error fetching starting cities:", err));
  }, []);

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest(".dest-dropdown-container")) {
        setIsDestOpen(false);
      }
      if (!event.target.closest(".startcity-dropdown-container")) {
        setIsStartCityOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const filteredDestinationsList = destinationsList.filter(dest =>
    dest.name.toLowerCase().includes(destSearch.toLowerCase()) ||
    (dest.country && dest.country.toLowerCase().includes(destSearch.toLowerCase()))
  );

  const filteredStartingCitiesList = startingCitiesList.filter(city =>
    city.name.toLowerCase().includes(startCitySearch.toLowerCase())
  );

  // Group starting cities by region
  const groupedStartingCities = startingCitiesList.reduce((acc, city) => {
    const region = city.region || "Other";
    if (!acc[region]) {
      acc[region] = [];
    }
    acc[region].push(city);
    return acc;
  }, {});

  // Helper to fix image URLs
  const getImageUrl = (path) => {
    if (!path) return "";
    if (path.startsWith("http")) return path;
    return `${path}`;
  };

  // ===================== FILTERED LIST =====================
  const filtered = packages.filter((pkg) => {
    const categoryMatch = category ? pkg.category === category : true;

    // Destination match
    const destinationMatch = !destination ? true : (
      pkg.destinations && pkg.destinations.some(d => d.name === destination)
    );

    // Ensure price is a number
    const price = Number(pkg.Offer_price || 0);

    return (
      categoryMatch &&
      destinationMatch &&
      (nights ? pkg.nights === Number(nights) : true) &&
      (startingCity ? pkg.starting_city === startingCity : true) &&
      price >= budget[0] &&
      price <= budget[1]
    );
  });

  return (
    <div className="w-full flex bg-gray-50 min-h-screen">

      {/* ====================== LEFT FILTER PANEL ====================== */}
      <div className="w-[25%] bg-white shadow-md p-6 sticky top-0 h-screen overflow-y-auto">
        <h3 className="text-xl font-semibold mb-4">Filters</h3>

        {/* DESTINATION (SEARCHABLE) */}
        <div className="mb-6 relative dest-dropdown-container">
          <label className="font-semibold block mb-2">Destination</label>

          <div
            className="w-full p-2 border rounded bg-white cursor-pointer flex justify-between items-center"
            onClick={() => setIsDestOpen(!isDestOpen)}
          >
            <span className={destination ? "text-gray-900" : "text-gray-500"}>
              {destination || "Any Destination"}
            </span>
            <span className="text-xs transition-transform duration-200" style={{ transform: isDestOpen ? 'rotate(180deg)' : 'rotate(0deg)' }}>
              Γû╝
            </span>
          </div>

          {isDestOpen && (
            <div className="absolute z-50 mt-1 w-full bg-white border rounded shadow-xl overflow-hidden animate-in fade-in zoom-in duration-200">
              <div className="p-2 border-b bg-gray-50">
                <input
                  type="text"
                  placeholder="Search destination..."
                  className="w-full p-2 text-sm border rounded focus:outline-none focus:ring-1 focus:ring-green-700"
                  value={destSearch}
                  onChange={(e) => setDestSearch(e.target.value)}
                  onClick={(e) => e.stopPropagation()}
                  autoFocus
                />
              </div>
              <ul className="max-h-60 overflow-y-auto py-1">
                <li
                  className="px-4 py-2 hover:bg-green-50 cursor-pointer text-sm"
                  onClick={() => {
                    setDestination("");
                    setIsDestOpen(false);
                    setDestSearch("");
                  }}
                >
                  Any Destination
                </li>
                {filteredDestinationsList.length > 0 ? (
                  filteredDestinationsList.map((dest) => (
                    <li
                      key={dest.id}
                      className={`px-4 py-2 hover:bg-green-50 cursor-pointer text-sm ${destination === dest.name ? 'bg-green-100 font-semibold' : ''}`}
                      onClick={() => {
                        setDestination(dest.name);
                        setIsDestOpen(false);
                        setDestSearch("");
                      }}
                    >
                      <div className="flex flex-col">
                        <span>{dest.name}</span>
                        {dest.country && <span className="text-[10px] text-gray-400 uppercase tracking-tighter">{dest.country}</span>}
                      </div>
                    </li>
                  ))
                ) : (
                  <li className="px-4 py-2 text-gray-500 text-sm italic">No results found</li>
                )}
              </ul>
            </div>
          )}
        </div>

        {/* NIGHTS */}
        <div className="mb-6">
          <label className="font-semibold">Total Nights</label>
          <select
            className="w-full p-2 border rounded mt-2"
            onChange={(e) => setNights(e.target.value)}
          >
            <option value="">Any</option>
            <option value="2">2 Nights</option>
            <option value="3">3 Nights</option>
            <option value="4">4 Nights</option>
            <option value="5">5 Nights</option>
            <option value="6">6 Nights</option>
            <option value="7">7 Nights</option>
            <option value="8">8 Nights</option>
            <option value="9">9 Nights</option>
            <option value="10">10 Nights</option>
            <option value="11">11 Nights</option>
            <option value="12">12 Nights</option>
            <option value="13">13 Nights</option>
            <option value="14">14 Nights</option>
            <option value="15">15 Nights</option>
            <option value="16">16 Nights</option>
            <option value="17">17 Nights</option>
            <option value="18">18 Nights</option>
            <option value="19">19 Nights</option>
            <option value="20">20 Nights</option>
            <option value="21">21 Nights</option>
            <option value="22">22 Nights</option>
            <option value="23">23 Nights</option>
            <option value="24">24 Nights</option>
            <option value="25">25 Nights</option>
            <option value="26">26 Nights</option>
            <option value="27">27 Nights</option>
            <option value="28">28 Nights</option>
            <option value="29">29 Nights</option>
            <option value="30">30 Nights</option>
          </select>
        </div>

        {/* STARTING CITY (SEARCHABLE) */}
        <div className="mb-6 relative startcity-dropdown-container">
          <label className="font-semibold block mb-2">Starting City</label>

          <div
            className="w-full p-2 border rounded bg-white cursor-pointer flex justify-between items-center"
            onClick={() => setIsStartCityOpen(!isStartCityOpen)}
          >
            <span className={startingCity ? "text-gray-900" : "text-gray-500"}>
              {startingCity || "Any Starting City"}
            </span>
            <span className="text-xs transition-transform duration-200" style={{ transform: isStartCityOpen ? 'rotate(180deg)' : 'rotate(0deg)' }}>
              Γû╝
            </span>
          </div>

          {isStartCityOpen && (
            <div className="absolute z-50 mt-1 w-full bg-white border rounded shadow-xl overflow-hidden animate-in fade-in zoom-in duration-200">
              <div className="p-2 border-b bg-gray-50">
                <input
                  type="text"
                  placeholder="Search starting city..."
                  className="w-full p-2 text-sm border rounded focus:outline-none focus:ring-1 focus:ring-green-700"
                  value={startCitySearch}
                  onChange={(e) => setStartCitySearch(e.target.value)}
                  onClick={(e) => e.stopPropagation()}
                  autoFocus
                />
              </div>
              <ul className="max-h-60 overflow-y-auto py-1">
                <li
                  className="px-4 py-2 hover:bg-green-50 cursor-pointer text-sm"
                  onClick={() => {
                    setStartingCity("");
                    setIsStartCityOpen(false);
                    setStartCitySearch("");
                  }}
                >
                  Any Starting City
                </li>
                {filteredStartingCitiesList.length > 0 ? (
                  filteredStartingCitiesList.map((city) => (
                    <li
                      key={city.id}
                      className={`px-4 py-2 hover:bg-green-50 cursor-pointer text-sm ${startingCity === city.name ? 'bg-green-100 font-semibold' : ''}`}
                      onClick={() => {
                        setStartingCity(city.name);
                        setIsStartCityOpen(false);
                        setStartCitySearch("");
                      }}
                    >
                      <div className="flex flex-col">
                        <span>{city.name}</span>
                        {city.region && <span className="text-[10px] text-gray-400 uppercase tracking-tighter">{city.region}</span>}
                      </div>
                    </li>
                  ))
                ) : (
                  <li className="px-4 py-2 text-gray-500 text-sm italic">No results found</li>
                )}
              </ul>
            </div>
          )}
        </div>

        {/* BUDGET */}
        <div className="mb-6">
          <label className="font-semibold">Budget</label>
          <input
            type="range"
            min="0"
            max="200000"
            value={budget[1]}
            onChange={(e) => setBudget([0, Number(e.target.value)])}
            className="w-full mt-2"
          />
          <p className="text-sm mt-1">Γé╣ {budget[0]} ΓÇô Γé╣ {budget[1]}</p>
        </div>

        {/* FLIGHT OPTION */}
        <div className="mb-6">
          <label className="font-semibold">Flight Option</label>
          <select
            className="w-full p-2 border rounded mt-2"
            onChange={(e) => setFlightOption(e.target.value)}
          >
            <option value="">Any</option>
            <option value="With Flight">With Flight</option>
            <option value="Without Flight">Without Flight</option>
          </select>
        </div>
      </div>

      {/* ====================== RIGHT SIDE (PACKAGE LISTING) ====================== */}
      <div className="w-[75%] p-8">

        <h2 className="text-2xl font-bold mb-6">Holiday Packages</h2>



        {filtered.map((pkg) => (
          <div
            key={pkg.id}
            className="bg-white rounded-xl shadow-md p-5 mb-6 flex gap-6 justify-between"
          >

            {/* IMAGE SECTION */}
            <div className="relative w-72 h-48 bg-gray-200 rounded-lg">
              <img
                src={getImageUrl(pkg.card_image)}
                onError={(e) => { e.target.src = "https://via.placeholder.com/300x200?text=No+Image" }}
                className="w-72 h-48 rounded-lg object-cover"
                alt={pkg.title}
              />

              {/* Days badge */}
              <span className="absolute top-2 left-2 bg-black/70 text-white text-xs px-2 py-1 rounded">
                {pkg.days} days
              </span>

              {/* Guaranteed Fixed Departure */}
              <span className="absolute bottom-2 left-2 bg-orange-600 text-white text-xs px-3 py-1 rounded">
                GUARANTEED FIXED DEPARTURE
              </span>
            </div>

            {/* MAIN DETAILS SECTION */}
            <div className="flex-1">

              <h3 className="text-xl font-semibold">{pkg.title}</h3>

              {/* LOCATION */}
              <p className="text-gray-600 text-sm mt-1">
                {pkg.starting_city}
                {pkg.destinations && pkg.destinations.length > 0 &&
                  ` ΓÇó ${pkg.destinations.map(d => `${d.name} (${d.nights}N)`).join(" ΓÇó ")}`}
              </p>

              {/* BULLETS */}
              <ul className="text-gray-700 text-sm mt-3 space-y-1">
                <li>ΓÇó Accommodation in all places as per itinerary</li>
                <li>ΓÇó Daily Breakfast & Dinner included</li>
                <li>ΓÇó All Tours and Transfers on private basis</li>
                <li>ΓÇó Sightseeing as per the itinerary</li>
                <li>ΓÇó All Entrance Fees included</li>
              </ul>



              {/* Departure info */}
              <div className="flex gap-12 mt-4 text-sm">
                <div>
                  <p className="text-gray-500">Departure Starting</p>
                  <p className="font-semibold">
                    {pkg.start_date
                      ? new Date(pkg.start_date).toLocaleDateString("en-GB", {
                        day: "2-digit",
                        month: "short",
                        year: "numeric",
                      })
                      : "Flexible Dates"}
                  </p>
                </div>

                <div>
                  <p className="text-gray-500">Max. Group Size</p>
                  <p className="font-semibold">{pkg.group_size}</p>
                </div>
              </div>
            </div>

            {/* PRICE & BUTTON */}
            <div className="text-right flex flex-col justify-between">

              <div>
                {pkg.price && (
                  <p className="line-through text-gray-400 text-sm">
                    Γé╣ {pkg.price.toLocaleString()}
                  </p>
                )}
                <p className="text-2xl font-bold">Γé╣ {(pkg.Offer_price || 0).toLocaleString()}</p>
                <p className="text-gray-500 text-xs">per person</p>
              </div>

              <button
                className="bg-[#14532d] text-white px-6 py-2 rounded-lg mt-4 text-sm"
                onClick={() => navigate(`/holiday/${pkg.id}`)}
              >
                View Details
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Holidays;

import React, { useState, useEffect } from "react";
import axios from "axios";
import { ExternalLink, RefreshCw, Settings } from "lucide-react";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";
import AdminCard from "../../components/admin/AdminCard";

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    destinations: 0,
    packages: 0,
    enquiries: 0,
    holidayEnquiries: 0,
    umrahEnquiries: 0,
    startingCities: 0,
    itineraryMasters: 0,
    nationalities: 0,
    umrahDestinations: 0,
  });
  const [recentEnquiries, setRecentEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);

  // Base API URL
  const API_BASE_URL = "/api";
  const DJANGO_ADMIN_URL = "/admin/";

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      console.log("Fetching dashboard data from Django API...");

      // Fetch all data in parallel with error handling for each endpoint
      const fetchPromises = [
        axios.get(`${API_BASE_URL}/destinations/`).catch(err => ({ error: err, endpoint: 'destinations' })),
        axios.get(`${API_BASE_URL}/packages/`).catch(err => ({ error: err, endpoint: 'packages' })),
        axios.get(`${API_BASE_URL}/enquiry-form/`).catch(err => ({ error: err, endpoint: 'enquiries' })),
        axios.get(`${API_BASE_URL}/holiday-form/`).catch(err => ({ error: err, endpoint: 'holiday-enquiries' })),
        axios.get(`${API_BASE_URL}/umrah-form/`).catch(err => ({ error: err, endpoint: 'umrah-enquiries' })),
        axios.get(`${API_BASE_URL}/starting-cities/`).catch(err => ({ error: err, endpoint: 'starting-cities' })),
        axios.get(`${API_BASE_URL}/itinerary-masters/`).catch(err => ({ error: err, endpoint: 'itinerary-masters' })),
        axios.get(`${API_BASE_URL}/nationalities/`).catch(err => ({ error: err, endpoint: 'nationalities' })),
        axios.get(`${API_BASE_URL}/umrah-destinations/`).catch(err => ({ error: err, endpoint: 'umrah-destinations' })),
      ];

      const responses = await Promise.all(fetchPromises);

      // Process responses and handle errors
      const newStats = {
        destinations: 0,
        packages: 0,
        enquiries: 0,
        holidayEnquiries: 0,
        umrahEnquiries: 0,
        startingCities: 0,
        itineraryMasters: 0,
        nationalities: 0,
        umrahDestinations: 0,
      };

      const allEnquiries = [];
      const errors = [];

      responses.forEach((response, index) => {
        const endpoints = ['destinations', 'packages', 'enquiries', 'holiday-enquiries', 'umrah-enquiries', 'starting-cities', 'itinerary-masters', 'nationalities', 'umrah-destinations'];
        const endpoint = endpoints[index];

        if (response.error) {
          console.error(`Error fetching ${endpoint}:`, response.error);
          errors.push(`${endpoint}: ${response.error.message}`);
        } else if (response.data) {
          const count = Array.isArray(response.data) ? response.data.length : 0;

          switch (endpoint) {
            case 'destinations':
              newStats.destinations = count;
              break;
            case 'packages':
              newStats.packages = count;
              break;
            case 'enquiries':
              newStats.enquiries = count;
              allEnquiries.push(...response.data.map(item => ({ ...item, type: 'General' })));
              break;
            case 'holiday-enquiries':
              newStats.holidayEnquiries = count;
              allEnquiries.push(...response.data.map(item => ({ ...item, type: 'Holiday' })));
              break;
            case 'umrah-enquiries':
              newStats.umrahEnquiries = count;
              allEnquiries.push(...response.data.map(item => ({ ...item, type: 'Umrah' })));
              break;
            case 'starting-cities':
              newStats.startingCities = count;
              break;
            case 'itinerary-masters':
              newStats.itineraryMasters = count;
              break;
            case 'nationalities':
              newStats.nationalities = count;
              break;
            case 'umrah-destinations':
              newStats.umrahDestinations = count;
              break;
          }
        }
      });

      setStats(newStats);

      // Sort enquiries by ID (most recent first) and take latest 5
      const sortedEnquiries = allEnquiries
        .sort((a, b) => (b.id || 0) - (a.id || 0))
        .slice(0, 5);

      setRecentEnquiries(sortedEnquiries);
      setLastUpdated(new Date());

      if (errors.length > 0) {
        setError(`Partial data loaded. Errors: ${errors.join(', ')}`);
      } else {
        setError(null);
      }

      console.log("Dashboard data loaded successfully:", { stats: newStats, enquiriesCount: sortedEnquiries.length });

    } catch (err) {
      console.error("Error fetching dashboard data:", err);
      setError(`Failed to load dashboard data: ${err.message}. Please check if the Django backend is running on ${API_BASE_URL}`);
    } finally {
      setLoading(false);
    }
  };

  const openDjangoAdmin = () => {
    window.open(DJANGO_ADMIN_URL, '_blank');
  };

  const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    return new Date(dateString).toLocaleDateString();
  };

  const getEnquiryName = (enquiry) => {
    if (enquiry.name) return enquiry.name;
    if (enquiry.first_name) return `${enquiry.first_name} ${enquiry.last_name || ""}`.trim();
    return "Unknown";
  };

  return (
    <div className="flex bg-gray-100 min-h-screen">
      <AdminSidebar />

      <div className="flex-1">
        <AdminTopbar />

        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-semibold text-gray-800">
              Welcome to Goimomi Admin Dashboard
            </h2>
            <div className="flex gap-3">
            </div>
          </div>



          {loading ? (
            <div className="text-center py-8">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-[#14532d]"></div>
              <p className="mt-2 text-gray-600">Loading dashboard data...</p>
            </div>
          ) : error ? (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-6">
              <p>{error}</p>
              <button
                onClick={fetchDashboardData}
                className="mt-2 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
              >
                Retry
              </button>
            </div>
          ) : (
            <>
              {/* Stats Cards */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-7 gap-4 mb-8">
                <AdminCard title="Destinations" count={stats.destinations} />
                <AdminCard title="Holiday Packages" count={stats.packages} />
                <AdminCard title="Starting Cities" count={stats.startingCities} />
                <AdminCard title="Itinerary Masters" count={stats.itineraryMasters} />
                <AdminCard title="General Enquiries" count={stats.enquiries} />
                <AdminCard title="Holiday Enquiries" count={stats.holidayEnquiries} />
                <AdminCard title="Umrah Enquiries" count={stats.umrahEnquiries} />
                <AdminCard title="Nationalities" count={stats.nationalities} />
                <AdminCard title="Umrah Destinations" count={stats.umrahDestinations} />
              </div>

              {/* Recent Enquiries */}
              <div className="bg-white rounded-xl shadow-md p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="font-semibold">Recent Enquiries</h3>
                  <button
                    onClick={fetchDashboardData}
                    className="text-sm bg-[#14532d] text-white px-3 py-1 rounded hover:bg-[#0f4a24]"
                  >
                    Refresh
                  </button>
                </div>

                {recentEnquiries.length > 0 ? (
                  <div className="overflow-x-auto">
                    <table className="w-full text-sm">
                      <thead>
                        <tr className="border-b">
                          <th className="text-left py-2">Name</th>
                          <th className="text-left py-2">Type</th>
                          <th className="text-left py-2">Contact</th>
                          <th className="text-left py-2">Date</th>
                        </tr>
                      </thead>
                      <tbody>
                        {recentEnquiries.map((enquiry, index) => (
                          <tr key={`${enquiry.type}-${enquiry.id}`} className="border-b hover:bg-gray-50">
                            <td className="py-2">{getEnquiryName(enquiry)}</td>
                            <td className="py-2">
                              <span className="px-2 py-1 bg-[#14532d] text-white text-xs rounded">
                                {enquiry.type}
                              </span>
                            </td>
                            <td className="py-2">{enquiry.email || enquiry.phone || 'N/A'}</td>
                            <td className="py-2">{formatDate(enquiry.created_at || enquiry.submitted_at)}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ) : (
                  <p className="text-gray-500 text-center py-4">No recent enquiries found</p>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";

const DestinationEdit = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [form, setForm] = useState({
        name: "",
        region: "",
        city: "",
        country: "",
        description: "",
        image: null,
    });
    const [existingImage, setExistingImage] = useState("");
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState("");
    const [error, setError] = useState("");

    const API_BASE_URL = "/api";

    useEffect(() => {
        fetchDestination();
    }, [id]);

    const fetchDestination = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`${API_BASE_URL}/destinations/${id}/`);
            const data = response.data;
            setForm({
                name: data.name || "",
                region: data.region || "",
                city: data.city || "",
                country: data.country || "",
                description: data.description || "",
                image: null,
            });
            setExistingImage(data.image || "");
        } catch (err) {
            console.error("Error fetching destination:", err);
            setError("Failed to load destination data.");
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        if (e.target.name === "image") {
            setForm({ ...form, image: e.target.files[0] });
        } else {
            setForm({ ...form, [e.target.name]: e.target.value });
        }
        setMessage("");
        setError("");
    };

    const handleSubmit = async (e, continueEditing = false) => {
        if (e) e.preventDefault();
        setLoading(true);
        setMessage("");
        setError("");

        const formData = new FormData();
        formData.append("name", form.name);
        formData.append("region", form.region);
        formData.append("city", form.city);
        formData.append("country", form.country);
        formData.append("description", form.description);

        if (form.image) {
            formData.append("image", form.image);
        }

        try {
            const response = await axios.put(`${API_BASE_URL}/destinations/${id}/`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            if (response.status === 200) {
                setMessage("Destination updated successfully!");
                if (response.data.image) {
                    setExistingImage(response.data.image);
                    setForm(prev => ({ ...prev, image: null }));
                    // Reset file input
                    const fileInput = document.getElementById("imageInput");
                    if (fileInput) fileInput.value = "";
                }

                if (!continueEditing) {
                    setTimeout(() => navigate("/admin/destinations"), 1500);
                }
            }
        } catch (err) {
            console.error("Error updating destination:", err);
            if (err.response?.data) {
                const errorMessages = Object.values(err.response.data).flat();
                setError(errorMessages.join(", "));
            } else {
                setError("Failed to update destination. Please try again.");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex bg-gray-100 min-h-screen">
            <AdminSidebar />

            <div className="flex-1">
                <AdminTopbar />

                <div className="p-6">
                    <div className="bg-white rounded-lg shadow-sm p-6 mb-6 flex justify-between items-center">
                        <div>
                            <h1 className="text-2xl font-bold text-gray-800 mb-2">Edit Destination</h1>
                            <p className="text-gray-600">Update destination details and images</p>
                        </div>
                        <button
                            onClick={() => navigate("/admin/destinations")}
                            className="bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300 transition"
                        >
                            Back to List
                        </button>
                    </div>

                    {message && (
                        <div className="mb-6 p-4 bg-green-50 border border-green-200 text-green-700 rounded-lg flex items-center gap-2">
                            <span className="font-bold">Γ£ô</span> {message}
                        </div>
                    )}
                    {error && (
                        <div className="mb-6 p-4 bg-red-50 border border-red-200 text-red-700 rounded-lg flex items-center gap-2">
                            <span className="font-bold">ΓÜá</span> {error}
                        </div>
                    )}

                    <form onSubmit={(e) => handleSubmit(e, false)} className="bg-white rounded-lg shadow-md overflow-hidden">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 p-6">
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-gray-700 font-semibold mb-2">Name:</label>
                                    <input
                                        name="name"
                                        value={form.name}
                                        onChange={handleChange}
                                        className="w-full border border-gray-300 px-4 py-2 rounded focus:ring-2 focus:ring-[#14532d] outline-none"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-gray-700 font-semibold mb-2">Region:</label>
                                    <input
                                        name="region"
                                        value={form.region}
                                        onChange={handleChange}
                                        className="w-full border border-gray-300 px-4 py-2 rounded focus:ring-2 focus:ring-[#14532d] outline-none"
                                    />
                                </div>
                                <div>
                                    <label className="block text-gray-700 font-semibold mb-2">City:</label>
                                    <input
                                        name="city"
                                        value={form.city}
                                        onChange={handleChange}
                                        className="w-full border border-gray-300 px-4 py-2 rounded focus:ring-2 focus:ring-[#14532d] outline-none"
                                    />
                                </div>
                                <div>
                                    <label className="block text-gray-700 font-semibold mb-2">Country:</label>
                                    <input
                                        name="country"
                                        value={form.country}
                                        onChange={handleChange}
                                        className="w-full border border-gray-300 px-4 py-2 rounded focus:ring-2 focus:ring-[#14532d] outline-none"
                                        required
                                    />
                                </div>
                            </div>

                            <div className="space-y-4">
                                <div>
                                    <label className="block text-gray-700 font-semibold mb-2">Description:</label>
                                    <textarea
                                        name="description"
                                        value={form.description}
                                        onChange={handleChange}
                                        rows="5"
                                        className="w-full border border-gray-300 px-4 py-2 rounded focus:ring-2 focus:ring-[#14532d] outline-none"
                                    />
                                </div>
                                <div>
                                    <label className="block text-gray-700 font-semibold mb-2">Image:</label>
                                    {existingImage && (
                                        <div className="mb-2">
                                            <p className="text-xs text-gray-500 mb-1">Current Image:</p>
                                            <img
                                                src={existingImage.startsWith('http') ? existingImage : `${existingImage}`}
                                                alt="Existing"
                                                className="h-20 w-32 object-cover rounded border"
                                            />
                                        </div>
                                    )}
                                    <input
                                        id="imageInput"
                                        type="file"
                                        name="image"
                                        onChange={handleChange}
                                        accept="image/*"
                                        className="w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded file:border-0 file:text-sm file:font-semibold file:bg-green-50 file:text-[#14532d] hover:file:bg-green-100"
                                    />
                                </div>
                            </div>
                        </div>

                        <div className="bg-gray-50 p-6 flex gap-3">
                            <button
                                type="submit"
                                className="bg-[#14532d] text-white px-6 py-2 rounded hover:bg-[#0f4a24] transition font-semibold disabled:opacity-50"
                                disabled={loading}
                            >
                                {loading ? "Updating..." : "UPDATE"}
                            </button>
                            <button
                                type="button"
                                onClick={(e) => handleSubmit(e, true)}
                                className="bg-[#1f7a45] text-white px-6 py-2 rounded hover:bg-[#1a6338] transition font-semibold disabled:opacity-50"
                                disabled={loading}
                            >
                                Save and continue editing
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default DestinationEdit;

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Edit2, Trash2, Plus, ArrowLeft, Search } from "lucide-react";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";

const DestinationManage = () => {
  const [destinations, setDestinations] = useState([]);
  const [filteredDestinations, setFilteredDestinations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [searchTerm, setSearchTerm] = useState("");

  const navigate = useNavigate();
  const API_BASE_URL = "/api";

  useEffect(() => {
    fetchDestinations();
  }, []);

  const fetchDestinations = async () => {
    try {
      setLoading(true);
      console.log("Fetching destinations from:", `${API_BASE_URL}/destinations/`);
      const response = await axios.get(`${API_BASE_URL}/destinations/`);
      console.log("API Response:", response.data);
      setDestinations(response.data);
      setFilteredDestinations(response.data);
      setError("");
    } catch (err) {
      console.error("Error fetching destinations:", err);
      setError(`Failed to load destinations: ${err.message}. Please check if the backend server is running on ${API_BASE_URL}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Filter destinations based on search term
    const filtered = destinations.filter(destination =>
      (destination.name || "").toLowerCase().includes(searchTerm.toLowerCase()) ||
      (destination.region || "").toLowerCase().includes(searchTerm.toLowerCase()) ||
      (destination.city || "").toLowerCase().includes(searchTerm.toLowerCase()) ||
      (destination.country || "").toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredDestinations(filtered);
  }, [searchTerm, destinations]);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const clearSearch = () => {
    setSearchTerm("");
  };

  const handleEdit = (destination) => {
    setEditingId(destination.id);
    setEditForm(destination);
    setMessage("");
    setError("");
  };

  const handleCancelEdit = () => {
    setEditingId(null);
    setEditForm({});
    setMessage("");
    setError("");
  };

  const handleUpdate = async (id) => {
    try {
      setLoading(true);
      await axios.put(`${API_BASE_URL}/destinations/${id}/`, editForm);
      setMessage("Destination updated successfully!");
      setEditingId(null);
      setEditForm({});
      fetchDestinations(); // Refresh list
    } catch (err) {
      console.error("Error updating destination:", err);
      if (err.response?.data) {
        const errorMessages = Object.values(err.response.data).flat();
        setError(errorMessages.join(", "));
      } else {
        setError("Failed to update destination. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this destination?")) {
      try {
        setLoading(true);
        await axios.delete(`${API_BASE_URL}/destinations/${id}/`);
        setMessage("Destination deleted successfully!");
        fetchDestinations(); // Refresh list
      } catch (err) {
        console.error("Error deleting destination:", err);
        setError("Failed to delete destination. Please try again.");
      } finally {
        setLoading(false);
      }
    }
  };

  const handleEditChange = (e) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  return (
    <div className="flex bg-gray-100 min-h-screen">
      <AdminSidebar />

      <div className="flex-1">
        <AdminTopbar />

        <div className="p-6">
          {/* Header */}
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-xl font-semibold text-gray-800">Manage Destinations</h1>
            <div className="flex gap-3">
              <button
                onClick={fetchDestinations}
                className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700 transition"
              >
                Refresh List
              </button>
              <button
                onClick={() => navigate("/admin/destinations/add")}
                className="flex items-center gap-2 bg-[#14532d] text-white px-4 py-2 rounded hover:bg-[#0f4a24] transition"
              >
                <Plus size={16} />
                Add New Destination
              </button>
            </div>
          </div>

          {/* Search Bar */}
          <div className="mb-6">
            <div className="relative max-w-md">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Search size={20} className="text-gray-400" />
              </div>
              <input
                type="text"
                value={searchTerm}
                onChange={handleSearch}
                placeholder="Search destinations by name, region, city, or country..."
                className="w-full pl-10 pr-10 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#14532d] focus:border-transparent"
              />
              {searchTerm && (
                <button
                  onClick={clearSearch}
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                >
                  <span className="text-gray-400 hover:text-gray-600">├ù</span>
                </button>
              )}
            </div>
            {searchTerm && (
              <p className="mt-2 text-sm text-gray-600">
                Found {filteredDestinations.length} destination{filteredDestinations.length !== 1 ? 's' : ''} matching "{searchTerm}"
              </p>
            )}
          </div>



          {/* Messages */}
          {message && (
            <div className="mb-4 p-4 bg-green-50 border border-green-200 text-green-700 rounded">
              {message}
            </div>
          )}
          {error && (
            <div className="mb-4 p-4 bg-red-50 border border-red-200 text-red-700 rounded">
              {error}
            </div>
          )}

          {/* Loading State */}
          {loading && destinations.length === 0 ? (
            <div className="text-center py-8">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-[#14532d]"></div>
              <p className="mt-2 text-gray-600">Loading destinations...</p>
            </div>
          ) : (
            /* Destinations List */
            <div className="bg-white rounded-lg shadow-md overflow-hidden">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-[#14532d] text-white">
                    <tr>
                      <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Name</th>
                      <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Region</th>
                      <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">City</th>
                      <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Country</th>
                      <th className="text-center py-4 px-6 font-semibold uppercase text-sm tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-200">
                    {filteredDestinations.length === 0 ? (
                      <tr>
                        <td colSpan="5" className="text-center py-10 text-gray-500">
                          {searchTerm ? `No destinations match "${searchTerm}"` : "No destinations found. Add one to get started!"}
                        </td>
                      </tr>
                    ) : (
                      filteredDestinations.map((destination) => (
                        <tr key={destination.id} className="hover:bg-gray-50 transition-colors">
                          <td className="py-4 px-6 font-medium text-gray-900 border-r">{destination.name}</td>
                          <td className="py-4 px-6 text-gray-600 border-r">{destination.region}</td>
                          <td className="py-4 px-6 text-gray-600 border-r">{destination.city}</td>
                          <td className="py-4 px-6 text-gray-600 border-r">{destination.country}</td>
                          <td className="py-4 px-6">
                            <div className="flex justify-center gap-4">
                              <button
                                onClick={() => navigate(`/admin/destinations/edit/${destination.id}`)}
                                className="flex items-center gap-1.5 bg-[#1f7a45] text-white px-4 py-1.5 rounded-md text-sm hover:bg-[#1a6338] transition shadow-sm"
                              >
                                <Edit2 size={14} />
                                Edit
                              </button>
                              <button
                                onClick={() => handleDelete(destination.id)}
                                className="flex items-center gap-1.5 bg-red-600 text-white px-4 py-1.5 rounded-md text-sm hover:bg-red-700 transition shadow-sm"
                              >
                                <Trash2 size={14} />
                                Delete
                              </button>
                            </div>
                          </td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          )}


        </div>
      </div>
    </div>
  );
};

export default DestinationManage;

import React, { useState, useEffect } from "react";
import axios from "axios";
import { Search, Eye, Trash2, Mail, Phone } from "lucide-react";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";

const EnquiryManage = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [filteredEnquiries, setFilteredEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedEnquiry, setSelectedEnquiry] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  const API_BASE_URL = "/api";

  useEffect(() => {
    fetchEnquiries();
  }, []);

  const fetchEnquiries = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/enquiry-form/`);
      setEnquiries(response.data);
      setFilteredEnquiries(response.data);
      setError("");
    } catch (err) {
      console.error("Error fetching enquiries:", err);
      setError(`Failed to load enquiries: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const filtered = enquiries.filter(enquiry =>
      enquiry.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.phone?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.message?.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredEnquiries(filtered);
  }, [searchTerm, enquiries]);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this enquiry?")) {
      try {
        setLoading(true);
        await axios.delete(`${API_BASE_URL}/enquiry-form/${id}/`);
        fetchEnquiries();
      } catch (err) {
        console.error("Error deleting enquiry:", err);
        setError("Failed to delete enquiry. Please try again.");
      } finally {
        setLoading(false);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <div className="flex bg-gray-100 min-h-screen">
      <AdminSidebar />
      <div className="flex-1">
        <AdminTopbar />
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-xl font-semibold text-gray-800">Manage General Enquiries</h1>
            <button
              onClick={fetchEnquiries}
              className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700 transition"
            >
              Refresh List
            </button>
          </div>

          {/* Search Bar */}
          <div className="mb-6">
            <div className="relative max-w-md">
              <Search size={20} className="absolute left-3 top-2.5 text-gray-400" />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search enquiries by name, email, phone, or message..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#14532d]"
              />
            </div>
          </div>

          {error && (
            <div className="mb-4 p-4 bg-red-50 border border-red-200 text-red-700 rounded">
              {error}
            </div>
          )}

          <div className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-[#14532d] text-white">
                  <tr>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Name</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Contact</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Message</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Date</th>
                    <th className="text-center py-4 px-6 font-semibold uppercase text-sm tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {filteredEnquiries.length === 0 ? (
                    <tr>
                      <td colSpan="5" className="text-center py-10 text-gray-500">
                        {searchTerm ? `No enquiries match "${searchTerm}"` : "No enquiries found."}
                      </td>
                    </tr>
                  ) : (
                    filteredEnquiries.map((enquiry) => (
                      <tr key={enquiry.id} className="hover:bg-gray-50 transition-colors">
                        <td className="py-4 px-6 font-medium text-gray-900 border-r">{enquiry.name}</td>
                        <td className="py-4 px-6 border-r">
                          <div className="space-y-1">
                            <div className="flex items-center gap-2 text-sm">
                              <Mail size={14} className="text-gray-400" />
                              {enquiry.email}
                            </div>
                            <div className="flex items-center gap-2 text-sm">
                              <Phone size={14} className="text-gray-400" />
                              {enquiry.phone}
                            </div>
                          </div>
                        </td>
                        <td className="py-3 px-4">
                          <div className="max-w-xs">
                            <p className="text-sm text-gray-600 truncate">
                              {enquiry.message}
                            </p>
                          </div>
                        </td>
                        <td className="py-3 px-4 text-sm">
                          {formatDate(enquiry.created_at)}
                        </td>
                        <td className="py-3 px-4">
                          <div className="flex gap-2">
                            <button
                              onClick={() => setSelectedEnquiry(enquiry)}
                              className="flex items-center gap-1 bg-blue-600 text-white px-3 py-1 rounded text-sm"
                            >
                              <Eye size={12} />
                              View
                            </button>
                            <button
                              onClick={() => handleDelete(enquiry.id)}
                              className="flex items-center gap-1 bg-red-600 text-white px-3 py-1 rounded text-sm"
                            >
                              <Trash2 size={12} />
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>

          {/* Enquiry Detail Modal */}
          {selectedEnquiry && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[80vh] overflow-y-auto">
                <div className="flex justify-between items-start mb-4">
                  <h2 className="text-xl font-semibold">Enquiry Details</h2>
                  <button
                    onClick={() => setSelectedEnquiry(null)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    ├ù
                  </button>
                </div>

                <div className="space-y-4">
                  <div>
                    <h3 className="font-semibold text-gray-700">Contact Information</h3>
                    <p><strong>Name:</strong> {selectedEnquiry.name}</p>
                    <p><strong>Phone:</strong> {selectedEnquiry.phone}</p>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-700">Timestamp</h3>
                    <p>Submitted: {formatDate(selectedEnquiry.created_at)}</p>
                  </div>
                </div>

                <div className="mt-6 flex gap-3">
                  <button
                    onClick={() => setSelectedEnquiry(null)}
                    className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
                  >
                    Close
                  </button>
                  <button
                    onClick={() => handleDelete(selectedEnquiry.id)}
                    className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                  >
                    Delete Enquiry
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EnquiryManage;

import React, { useState, useEffect } from "react";
import axios from "axios";
import { Search, Eye, Trash2, Mail, Phone, Calendar } from "lucide-react";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";
import { useNavigate } from "react-router-dom";

const HolidayEnquiryManage = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [filteredEnquiries, setFilteredEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedEnquiry, setSelectedEnquiry] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  const API_BASE_URL = "/api";

  useEffect(() => {
    fetchEnquiries();
  }, []);

  const fetchEnquiries = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/holiday-form/`);
      setEnquiries(response.data);
      setFilteredEnquiries(response.data);
      setError("");
    } catch (err) {
      console.error("Error fetching holiday enquiries:", err);
      setError(`Failed to load holiday enquiries: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const filtered = enquiries.filter(enquiry =>
      enquiry.first_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.last_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.phone?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.destination?.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredEnquiries(filtered);
  }, [searchTerm, enquiries]);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this holiday enquiry?")) {
      try {
        setLoading(true);
        await axios.delete(`${API_BASE_URL}/holiday-form/${id}/`);
        fetchEnquiries();
      } catch (err) {
        console.error("Error deleting holiday enquiry:", err);
        setError("Failed to delete holiday enquiry. Please try again.");
      } finally {
        setLoading(false);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <div className="flex bg-gray-100 min-h-screen">
      <AdminSidebar />
      <div className="flex-1">
        <AdminTopbar />
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-xl font-semibold text-gray-800">Select holiday enquiry to change</h1>
            <div className="flex gap-4">
              <button
                onClick={fetchEnquiries}
                className="bg-gray-100 border border-gray-300 text-gray-600 px-4 py-2 rounded font-medium hover:bg-gray-200 transition text-sm"
              >
                Refresh
              </button>
              <button
                onClick={() => navigate("/admin/holiday-enquiries/add")}
                className="bg-[#14532d] text-white px-4 py-2 rounded font-medium hover:bg-[#0f4a24] transition text-sm uppercase tracking-wider"
              >
                Add Holiday Enquiry
              </button>
            </div>
          </div>

          {/* Search Bar */}
          <div className="mb-6">
            <div className="relative max-w-md">
              <Search size={20} className="absolute left-3 top-2.5 text-gray-400" />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search holiday enquiries by name, email, phone, or destination..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#14532d]"
              />
            </div>
          </div>

          {error && (
            <div className="mb-4 p-4 bg-red-50 border border-red-200 text-red-700 rounded">
              {error}
            </div>
          )}

          <div className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-[#14532d] text-white">
                  <tr>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Name</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Contact</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Destination</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Travel Date</th>
                    <th className="text-center py-4 px-6 font-semibold uppercase text-sm tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {filteredEnquiries.length === 0 ? (
                    <tr>
                      <td colSpan="5" className="text-center py-10 text-gray-500">
                        {searchTerm ? `No holiday enquiries match "${searchTerm}"` : "No holiday enquiries found."}
                      </td>
                    </tr>
                  ) : (
                    filteredEnquiries.map((enquiry) => (
                      <tr key={enquiry.id} className="hover:bg-gray-50 transition-colors">
                        <td className="py-4 px-6 font-medium text-gray-900 border-r">
                          {enquiry.first_name} {enquiry.last_name}
                        </td>
                        <td className="py-4 px-6 border-r text-gray-600">
                          <div className="space-y-1">
                            <div className="flex items-center gap-2 text-sm">
                              <Mail size={14} className="text-gray-400" />
                              {enquiry.email}
                            </div>
                            <div className="flex items-center gap-2 text-sm">
                              <Phone size={14} className="text-gray-400" />
                              {enquiry.phone}
                            </div>
                          </div>
                        </td>
                        <td className="py-3 px-4">{enquiry.destination}</td>
                        <td className="py-3 px-4 text-sm">
                          <div className="flex items-center gap-2">
                            <Calendar size={14} className="text-gray-400" />
                            {formatDate(enquiry.travel_date)}
                          </div>
                        </td>
                        <td className="py-3 px-4">
                          <div className="flex gap-2">
                            <button
                              onClick={() => setSelectedEnquiry(enquiry)}
                              className="flex items-center gap-1 bg-blue-600 text-white px-3 py-1 rounded text-sm"
                            >
                              <Eye size={12} />
                              View
                            </button>
                            <button
                              onClick={() => handleDelete(enquiry.id)}
                              className="flex items-center gap-1 bg-red-600 text-white px-3 py-1 rounded text-sm"
                            >
                              <Trash2 size={12} />
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>

          {/* Enquiry Detail Modal */}
          {selectedEnquiry && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[80vh] overflow-y-auto">
                <div className="flex justify-between items-start mb-4">
                  <h2 className="text-xl font-semibold">Holiday Enquiry Details</h2>
                  <button
                    onClick={() => setSelectedEnquiry(null)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    ├ù
                  </button>
                </div>

                <div className="space-y-4">
                  <div>
                    <h3 className="font-semibold text-gray-700">Personal Information</h3>
                    <p><strong>Name:</strong> {selectedEnquiry.first_name} {selectedEnquiry.last_name}</p>
                    <p><strong>Email:</strong> {selectedEnquiry.email}</p>
                    <p><strong>Phone:</strong> {selectedEnquiry.phone}</p>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-700">Travel Details</h3>
                    <p><strong>Destination:</strong> {selectedEnquiry.destination}</p>
                    <p><strong>Travel Date:</strong> {formatDate(selectedEnquiry.travel_date)}</p>
                    <p><strong>Duration:</strong> {selectedEnquiry.duration} days</p>
                    <p><strong>Budget:</strong> ${selectedEnquiry.budget}</p>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-700">Additional Information</h3>
                    <p><strong>Adults:</strong> {selectedEnquiry.adults}</p>
                    <p><strong>Children:</strong> {selectedEnquiry.children}</p>
                    <p><strong>Message:</strong></p>
                    <p className="text-gray-600 whitespace-pre-wrap">{selectedEnquiry.message}</p>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-700">Timestamp</h3>
                    <p>Submitted: {formatDate(selectedEnquiry.created_at)}</p>
                  </div>
                </div>

                <div className="mt-6 flex gap-3">
                  <button
                    onClick={() => setSelectedEnquiry(null)}
                    className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
                  >
                    Close
                  </button>
                  <button
                    onClick={() => handleDelete(selectedEnquiry.id)}
                    className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                  >
                    Delete Enquiry
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default HolidayEnquiryManage;

import React, { useState, useEffect, useMemo, useRef } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";

/* ---------- UI helpers (same as Add) ---------- */
const Section = ({ title, children, className = "bg-white border border-gray-300 p-4" }) => (
    <div className="mb-6">
        <div className="bg-[#14532d] text-white px-4 py-2 font-semibold text-sm uppercase">
            {title}
        </div>
        <div className={className}>{children}</div>
    </div>
);

const Input = (props) => (
    <input
        {...props}
        className={`bg-white border border-gray-300 px-3 py-2 rounded w-full text-gray-800 focus:outline-none focus:ring-2 focus:ring-[#14532d] focus:border-transparent ${props.className || ''}`}
    />
);

const SearchableSelect = ({ options, value, onChange, placeholder = "Select..." }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [searchTerm, setSearchTerm] = useState("");
    const wrapperRef = useRef(null);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
                setIsOpen(false);
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => document.removeEventListener("mousedown", handleClickOutside);
    }, []);

    const filteredOptions = options.filter(option =>
        option.label.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const selectedOption = options.find(o => o.value === value);

    return (
        <div className="relative w-full" ref={wrapperRef}>
            <div
                className="bg-white border border-gray-300 px-3 py-2 rounded w-full text-gray-800 cursor-pointer flex justify-between items-center focus:ring-2 focus:ring-[#14532d]"
                onClick={() => setIsOpen(!isOpen)}
            >
                <span>{selectedOption ? selectedOption.label : placeholder}</span>
                <span className="text-gray-400 text-xs">Γû╝</span>
            </div>

            {isOpen && (
                <div className="absolute z-50 w-full mt-1 bg-white border border-gray-300 rounded shadow-lg max-h-60 overflow-hidden flex flex-col">
                    <div className="p-2 border-b border-gray-200 sticky top-0 bg-white">
                        <input
                            type="text"
                            className="w-full border border-gray-300 rounded px-2 py-1 text-sm focus:outline-none focus:border-[#14532d]"
                            placeholder="Search..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            onClick={(e) => e.stopPropagation()}
                            autoFocus
                        />
                    </div>
                    <div className="overflow-y-auto flex-1">
                        {filteredOptions.length > 0 ? (
                            filteredOptions.map((option) => (
                                <div
                                    key={option.value}
                                    className={`px-3 py-2 text-sm cursor-pointer hover:bg-gray-100 ${option.value === value ? "bg-green-50 text-[#14532d]" : "text-gray-800"}`}
                                    onClick={() => {
                                        onChange(option.value);
                                        setIsOpen(false);
                                    }}
                                >
                                    {option.label}
                                </div>
                            ))
                        ) : (
                            <div className="px-3 py-2 text-sm text-gray-500">No results found</div>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

const HolidayPackageEdit = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    const [packageDestinations, setPackageDestinations] = useState([]);
    const [itineraryDays, setItineraryDays] = useState([]);
    const [inclusions, setInclusions] = useState([]);
    const [exclusions, setExclusions] = useState([]);

    // Form state
    const [formData, setFormData] = useState({
        title: "",
        description: "",
        category: "",
        starting_city: "",
        days: "",
        start_date: "",
        group_size: 0,
        offer_price: "",
        price: "",
        header_image: null,
        card_image: null,
    });

    // Previews for existing images to show if no new file selected
    const [headerPreview, setHeaderPreview] = useState(null);
    const [cardPreview, setCardPreview] = useState(null);

    const [startingCities, setStartingCities] = useState([]);
    const [destinations, setDestinations] = useState([]);
    const [itineraryMasters, setItineraryMasters] = useState([]);
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [message, setMessage] = useState("");
    const [error, setError] = useState("");

    const API_BASE_URL = "/api";

    // Compute grouped starting cities
    const groupedStartingCities = useMemo(() => {
        const groups = startingCities.reduce((acc, city) => {
            const region = (city.region || "Other").toString().trim() || "Other";
            const key = region.toUpperCase();
            if (!acc[key]) acc[key] = [];
            acc[key].push(city);
            return acc;
        }, {});

        Object.keys(groups).forEach((key) => {
            groups[key].sort((a, b) => (a?.name || "").localeCompare(b?.name || ""));
        });

        const ordered = {};
        Object.keys(groups)
            .sort((a, b) => a.localeCompare(b))
            .forEach((key) => {
                ordered[key] = groups[key];
            });

        return ordered;
    }, [startingCities]);


    useEffect(() => {
        const loadData = async () => {
            try {
                setLoading(true);
                // Fetch dependencies in parallel
                const [citiesRes, destRes, mastersRes, pkgRes] = await Promise.all([
                    axios.get(`${API_BASE_URL}/starting-cities/`),
                    axios.get(`${API_BASE_URL}/destinations/`),
                    axios.get(`${API_BASE_URL}/itinerary-masters/`),
                    axios.get(`${API_BASE_URL}/packages/${id}/`),
                ]);

                setStartingCities(citiesRes.data);
                setDestinations(destRes.data);
                setItineraryMasters(mastersRes.data);

                // Populate Form Data
                const pkg = pkgRes.data;
                setFormData({
                    title: pkg.title || "",
                    description: pkg.description || "",
                    category: pkg.category || "",
                    starting_city: pkg.starting_city || "",
                    days: pkg.days || "",
                    start_date: pkg.start_date || "",
                    group_size: pkg.group_size || 0,
                    offer_price: pkg.Offer_price || "", // Note capitalization in model
                    price: pkg.price || "",
                    header_image: null, // Keep null unless changing
                    card_image: null,
                });

                // Set Previews
                setHeaderPreview(pkg.header_image);
                setCardPreview(pkg.card_image);

                // Populate Nested Data
                // Destinations
                if (pkg.destinations && Array.isArray(pkg.destinations)) {
                    setPackageDestinations(pkg.destinations.map(d => ({
                        destination: d.name, // Serializer returns name in 'name' field
                        nights: d.nights
                    })));
                }

                // Itinerary
                if (pkg.itinerary && Array.isArray(pkg.itinerary)) {
                    setItineraryDays(pkg.itinerary.map(day => ({
                        day: day.day_number,
                        title: day.title,
                        description: day.description,
                        master_template: day.master_template || "",
                        image: null, // We generally don't pre-fill file inputs. 
                        existing_image: day.image // Can show preview if needed
                    })));
                }

                // Inclusions
                if (pkg.inclusions && Array.isArray(pkg.inclusions)) {
                    setInclusions(pkg.inclusions.map(i => i.text));
                }

                // Exclusions
                if (pkg.exclusions && Array.isArray(pkg.exclusions)) {
                    setExclusions(pkg.exclusions.map(e => e.text));
                }

            } catch (err) {
                console.error("Error loading package data:", err);
                setError("Failed to load package details.");
            } finally {
                setLoading(false);
            }
        };

        if (id) {
            loadData();
        }
    }, [id]);


    useEffect(() => {
        // NOTE: Logic to sync itinerary days count with 'days' input is tricky in edit mode
        // because we don't want to accidentally delete fetched data if the fetched 'days' count is weird.
        // But generally we should respect the 'days' input.
        // For now, let's just let the user manually add/remove rows or let the input drive it carefully.
        // We'll reimplement the sync but ensure we don't wipe data immediately on load.
        // Actually, let's strictly stick to the Add logic: if user changes number, we adjust rows.
        // But wait, initial load sets 'days'.
    }, [formData.days]);

    // Sync logic similar to Add, but be careful not to trigger on initial load if states aren't ready.
    // We can skip this automatic sync for Edit to avoid accidental data loss, or implement it carefully.
    // Let's implement a manual "Update Rows" button or just let them add/remove manually?
    // The Add page had auto-sync. Let's keep it but only if rows.length != days
    useEffect(() => {
        if (loading) return;
        const dayCount = parseInt(formData.days, 10);
        if (!isNaN(dayCount) && dayCount >= 0) {
            setItineraryDays(prev => {
                if (prev.length === dayCount) return prev;
                if (dayCount > prev.length) {
                    const newDays = [];
                    for (let i = prev.length + 1; i <= dayCount; i++) {
                        newDays.push({
                            day: i.toString(), title: "", description: "", master_template: "", image: null
                        });
                    }
                    return [...prev, ...newDays];
                } else {
                    return prev.slice(0, dayCount);
                }
            });
        }
    }, [formData.days, loading]);


    /* ---------- handlers ---------- */
    const addRow = (setter, row) => setter((p) => [...p, row]);
    const removeRow = (setter, index) =>
        setter((p) => p.filter((_, i) => i !== index));

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        setFormData((prev) => ({ ...prev, [name]: files[0] }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSaving(true);
        setMessage("");
        setError("");

        try {
            const formDataToSend = new FormData();

            // Add basic fields
            formDataToSend.append("title", formData.title);
            formDataToSend.append("description", formData.description);
            formDataToSend.append("category", formData.category);
            formDataToSend.append("starting_city", formData.starting_city);
            // Ensure 'days' matches the actual number of itinerary rows
            formDataToSend.append("days", itineraryDays.length);
            if (formData.start_date) formDataToSend.append("start_date", formData.start_date);
            formDataToSend.append("group_size", formData.group_size);
            formDataToSend.append("Offer_price", formData.offer_price);
            if (formData.price) formDataToSend.append("price", formData.price);

            // Add main images ONLY if new file selected
            if (formData.header_image instanceof File) {
                formDataToSend.append("header_image", formData.header_image);
            }
            if (formData.card_image instanceof File) {
                formDataToSend.append("card_image", formData.card_image);
            }

            // Add package destinations
            formDataToSend.append("package_destinations", JSON.stringify(packageDestinations));

            // Add itinerary days JSON
            const itineraryJson = itineraryDays.map(day => ({
                day: day.day,
                title: day.title,
                description: day.description,
                master_template: day.master_template
            }));
            formDataToSend.append("itinerary_days", JSON.stringify(itineraryJson));

            // Add itinerary images
            itineraryDays.forEach((day, index) => {
                if (day.image instanceof File) {
                    formDataToSend.append(`itinerary_image_${index}`, day.image);
                }
            });

            // Add inclusions and exclusions
            formDataToSend.append("inclusions", JSON.stringify(inclusions.filter(i => i && i.trim() !== "")));
            formDataToSend.append("exclusions", JSON.stringify(exclusions.filter(e => e && e.trim() !== "")));

            // Use PUT to update
            const response = await axios.put(`${API_BASE_URL}/packages/${id}/`, formDataToSend, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            if (response.status === 200) {
                setMessage("Holiday package updated successfully!");
                window.scrollTo(0, 0);
            }
        } catch (err) {
            console.error("Error updating package:", err);
            if (err.response?.data) {
                // Handle varied error structures
                let msg = "Failed to update package.";
                try {
                    msg = JSON.stringify(err.response.data);
                } catch (e) { }
                setError(msg);
            } else {
                setError("Failed to update package. Please try again.");
            }
        } finally {
            setSaving(false);
        }
    };

    const handleMasterTemplateChange = (index, templateId) => {
        const copy = [...itineraryDays];
        copy[index].master_template = templateId;
        if (templateId) {
            const template = itineraryMasters.find(t => t.id === parseInt(templateId));
            if (template) {
                copy[index].title = template.title || "";
                copy[index].description = template.description || "";
            }
        }
        setItineraryDays(copy);
    };

    if (loading) {
        return (
            <div className="flex bg-gray-100 min-h-screen">
                <AdminSidebar />
                <div className="flex-1 p-10 flex justify-center items-center">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-700"></div>
                </div>
            </div>
        );
    }

    return (
        <div className="flex bg-gray-100 min-h-screen">
            <AdminSidebar />

            <div className="flex-1">
                <AdminTopbar />

                <div className="p-6">
                    <div className="bg-white rounded-lg shadow-sm p-6 mb-6 flex justify-between items-center">
                        <div>
                            <h1 className="text-2xl font-bold text-gray-800 mb-2">Edit Holiday Package</h1>
                            <p className="text-gray-600">Update package details</p>
                        </div>
                        <button
                            onClick={() => navigate('/admin/packages')}
                            className="bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300"
                        >
                            Back to List
                        </button>
                    </div>

                    {message && (
                        <div className="mb-6 p-4 bg-green-50 border border-green-200 text-green-700 rounded-lg">
                            {message}
                        </div>
                    )}
                    {error && (
                        <div className="mb-6 p-4 bg-red-50 border border-red-200 text-red-700 rounded-lg">
                            {error}
                        </div>
                    )}

                    <form onSubmit={handleSubmit}>
                        {/* PACKAGE INFORMATION */}
                        <Section title="Package Information">
                            <div className="grid grid-cols-1 gap-4">
                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Title:</span>
                                    <Input
                                        name="title"
                                        value={formData.title}
                                        onChange={handleInputChange}
                                        required
                                    />
                                </label>

                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Description:</span>
                                    <textarea
                                        name="description"
                                        value={formData.description}
                                        onChange={handleInputChange}
                                        className="bg-white border border-gray-300 p-3 rounded w-full h-32 text-gray-800 focus:outline-none focus:ring-2 focus:ring-[#14532d] focus:border-transparent"
                                        required
                                    />
                                </label>

                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Category:</span>
                                    <select
                                        name="category"
                                        value={formData.category}
                                        onChange={handleInputChange}
                                        className="bg-white border border-gray-300 p-2 rounded w-60 text-gray-800 focus:outline-none focus:ring-2 focus:ring-[#14532d] focus:border-transparent"
                                        required
                                    >
                                        <option value="">Select category</option>
                                        <option value="Domestic">Domestic</option>
                                        <option value="International">International</option>
                                        <option value="Umrah">Umrah</option>
                                    </select>
                                </label>
                            </div>
                        </Section>

                        {/* LOCATION DETAILS */}
                        <Section title="Location Details">
                            <label className="block">
                                <span className="text-gray-700 font-medium mb-1 block">Starting city:</span>
                                <select
                                    name="starting_city"
                                    value={formData.starting_city}
                                    onChange={handleInputChange}
                                    className="bg-white border border-gray-300 p-2 rounded w-60 text-gray-800 focus:outline-none focus:ring-2 focus:ring-[#14532d] focus:border-transparent"
                                    required
                                >
                                    <option value="">----------</option>
                                    {Object.entries(groupedStartingCities).map(([region, cities]) => (
                                        <optgroup key={region} label={region}>
                                            {cities.map((city) => (
                                                <option key={city.id} value={city.name}>
                                                    {city.name}
                                                </option>
                                            ))}
                                        </optgroup>
                                    ))}
                                </select>
                            </label>
                        </Section>

                        {/* DURATION */}
                        <Section title="Duration & Dates">
                            <div className="grid grid-cols-3 gap-4">
                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Days:</span>
                                    <Input
                                        type="number"
                                        name="days"
                                        value={formData.days}
                                        onChange={handleInputChange}
                                        required
                                    />
                                </label>
                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Start date:</span>
                                    <Input
                                        type="date"
                                        name="start_date"
                                        value={formData.start_date}
                                        onChange={handleInputChange}
                                    />
                                </label>
                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Group size:</span>
                                    <Input
                                        type="number"
                                        name="group_size"
                                        value={formData.group_size}
                                        onChange={handleInputChange}
                                    />
                                </label>
                            </div>
                        </Section>

                        {/* PRICING */}
                        <Section title="Pricing">
                            <div className="grid grid-cols-2 gap-4">
                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Offer price:</span>
                                    <Input
                                        type="number"
                                        name="offer_price"
                                        value={formData.offer_price}
                                        onChange={handleInputChange}
                                        required
                                    />
                                </label>
                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Price:</span>
                                    <Input
                                        type="number"
                                        name="price"
                                        value={formData.price}
                                        onChange={handleInputChange}
                                    />
                                </label>
                            </div>
                        </Section>

                        {/* IMAGES */}
                        <Section title="Images">
                            <div className="grid grid-cols-2 gap-6">
                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Header image:</span>
                                    {headerPreview && (
                                        <div className="mb-2">
                                            <img src={headerPreview} alt="Header Preview" className="h-32 object-cover rounded border" />
                                            <p className="text-xs text-gray-500">Current Image</p>
                                        </div>
                                    )}
                                    <Input
                                        type="file"
                                        name="header_image"
                                        onChange={handleFileChange}
                                        accept="image/*"
                                    />
                                    <p className="text-xs text-gray-500 mt-1">Leave blank to keep current image</p>
                                </label>
                                <label className="block">
                                    <span className="text-gray-700 font-medium mb-1 block">Card image:</span>
                                    {cardPreview && (
                                        <div className="mb-2">
                                            <img src={cardPreview} alt="Card Preview" className="h-32 object-cover rounded border" />
                                            <p className="text-xs text-gray-500">Current Image</p>
                                        </div>
                                    )}
                                    <Input
                                        type="file"
                                        name="card_image"
                                        onChange={handleFileChange}
                                        accept="image/*"
                                    />
                                    <p className="text-xs text-gray-500 mt-1">Leave blank to keep current image</p>
                                </label>
                            </div>
                        </Section>

                        {/* PACKAGE DESTINATIONS */}
                        <Section title="Package Destinations">
                            {packageDestinations.map((row, i) => (
                                <div key={i} className="grid grid-cols-4 gap-4 mb-3 items-center">
                                    <SearchableSelect
                                        options={destinations.map(d => ({ value: d.name, label: d.name }))}
                                        value={row.destination}
                                        onChange={(val) => {
                                            const copy = [...packageDestinations];
                                            copy[i].destination = val;
                                            setPackageDestinations(copy);
                                        }}
                                        placeholder="Select destination"
                                    />

                                    <Input
                                        type="number"
                                        placeholder="Nights"
                                        value={row.nights}
                                        onChange={(e) => {
                                            const copy = [...packageDestinations];
                                            copy[i].nights = e.target.value;
                                            setPackageDestinations(copy);
                                        }}
                                    />

                                    <button
                                        type="button"
                                        onClick={() => removeRow(setPackageDestinations, i)}
                                        className="text-red-600 hover:text-red-800 font-bold"
                                    >
                                        Γ£û Remove
                                    </button>
                                </div>
                            ))}

                            <div className="flex items-center gap-4 mt-2">
                                <button
                                    type="button"
                                    onClick={() =>
                                        addRow(setPackageDestinations, { destination: "", nights: 1 })
                                    }
                                    className="text-[#14532d] hover:text-[#0f4a24] font-semibold"
                                >
                                    + Add another Package destination
                                </button>
                            </div>
                        </Section>

                        {/* ITINERARY DAYS */}
                        <Section title="Itinerary Days" className="bg-white border border-gray-300 p-0">
                            <div className="text-gray-800">
                                <div className="grid grid-cols-12 gap-4 px-4 py-2 bg-[#e6f0eb] text-[#14532d] text-xs font-bold uppercase tracking-wider border-b border-green-100">
                                    <div className="col-span-1">Day</div>
                                    <div className="col-span-3">Master Template</div>
                                    <div className="col-span-3">Title</div>
                                    <div className="col-span-3">Description</div>
                                    <div className="col-span-2">Image</div>
                                </div>

                                {itineraryDays.map((row, i) => (
                                    <div key={i} className="grid grid-cols-12 gap-4 px-4 py-4 border-b border-gray-200 items-start hover:bg-gray-50 transition-colors">
                                        <div className="col-span-1">
                                            <input
                                                type="number"
                                                placeholder="#"
                                                value={row.day}
                                                onChange={(e) => {
                                                    const copy = [...itineraryDays];
                                                    copy[i].day = e.target.value;
                                                    setItineraryDays(copy);
                                                }}
                                                className="w-full bg-white border border-gray-300 text-gray-800 px-2 py-1 rounded focus:border-[#14532d] focus:ring-1 focus:ring-[#14532d] text-sm"
                                            />
                                        </div>

                                        <div className="col-span-3">
                                            <select
                                                value={row.master_template}
                                                onChange={(e) => handleMasterTemplateChange(i, e.target.value)}
                                                className="w-full bg-white border border-gray-300 text-gray-800 px-2 py-1 rounded focus:border-[#14532d] text-sm"
                                            >
                                                <option value="">Select Template...</option>
                                                {itineraryMasters.map(master => (
                                                    <option key={master.id} value={master.id}>{master.name}</option>
                                                ))}
                                            </select>
                                            <div className="flex gap-2 mt-2 text-gray-400 text-xs text-right w-full justify-end">
                                                <button
                                                    type="button"
                                                    onClick={() => removeRow(setItineraryDays, i)}
                                                    className="text-red-500 hover:text-red-700 font-bold"
                                                >
                                                    Γ£û Remove
                                                </button>
                                            </div>
                                        </div>

                                        <div className="col-span-3">
                                            <input
                                                type="text"
                                                value={row.title}
                                                onChange={(e) => {
                                                    const copy = [...itineraryDays];
                                                    copy[i].title = e.target.value;
                                                    setItineraryDays(copy);
                                                }}
                                                className="w-full bg-white border border-gray-300 text-gray-800 px-2 py-1 rounded focus:border-[#14532d] text-sm"
                                            />
                                        </div>

                                        <div className="col-span-3">
                                            <textarea
                                                value={row.description}
                                                onChange={(e) => {
                                                    const copy = [...itineraryDays];
                                                    copy[i].description = e.target.value;
                                                    setItineraryDays(copy);
                                                }}
                                                rows="4"
                                                className="w-full bg-white border border-gray-300 text-gray-800 px-2 py-1 rounded focus:border-[#14532d] text-sm"
                                            />
                                        </div>

                                        <div className="col-span-2">
                                            {row.existing_image && !row.image && (
                                                <div className="mb-2">
                                                    <img src={row.existing_image} alt="Day" className="h-16 w-full object-cover rounded" />
                                                </div>
                                            )}
                                            <input
                                                type="file"
                                                accept="image/*"
                                                onChange={(e) => {
                                                    const copy = [...itineraryDays];
                                                    copy[i].image = e.target.files[0];
                                                    setItineraryDays(copy);
                                                }}
                                                className="w-full text-xs text-gray-500"
                                            />
                                        </div>
                                    </div>
                                ))}

                                <div className="p-4 bg-gray-50 border-t border-gray-200">
                                    <button
                                        type="button"
                                        onClick={() =>
                                            addRow(setItineraryDays, {
                                                day: (itineraryDays.length + 1).toString(),
                                                title: "",
                                                description: "",
                                                master_template: "",
                                                image: null
                                            })
                                        }
                                        className="flex items-center gap-1 text-[#14532d] hover:text-[#0f4a24] font-semibold text-sm"
                                    >
                                        <span>+</span> Add another Itinerary day
                                    </button>
                                </div>
                            </div>
                        </Section>

                        {/* INCLUSIONS */}
                        <Section title="Inclusions">
                            {inclusions.map((inc, i) => (
                                <div key={i} className="flex gap-4 mb-2">
                                    <Input
                                        value={inc}
                                        onChange={(e) => {
                                            const copy = [...inclusions];
                                            copy[i] = e.target.value;
                                            setInclusions(copy);
                                        }}
                                    />
                                    <button
                                        type="button"
                                        onClick={() => removeRow(setInclusions, i)}
                                        className="text-red-600 hover:text-red-800 font-bold whitespace-nowrap"
                                    >
                                        Γ£û Remove
                                    </button>
                                </div>
                            ))}
                            <button
                                type="button"
                                onClick={() => addRow(setInclusions, "")}
                                className="text-[#14532d] hover:text-[#0f4a24] font-semibold"
                            >
                                + Add another Inclusion
                            </button>
                        </Section>

                        {/* EXCLUSIONS */}
                        <Section title="Exclusions">
                            {exclusions.map((exc, i) => (
                                <div key={i} className="flex gap-4 mb-2">
                                    <Input
                                        value={exc}
                                        onChange={(e) => {
                                            const copy = [...exclusions];
                                            copy[i] = e.target.value;
                                            setExclusions(copy);
                                        }}
                                    />
                                    <button
                                        type="button"
                                        onClick={() => removeRow(setExclusions, i)}
                                        className="text-red-600 hover:text-red-800 font-bold whitespace-nowrap"
                                    >
                                        Γ£û Remove
                                    </button>
                                </div>
                            ))}
                            <button
                                type="button"
                                onClick={() => addRow(setExclusions, "")}
                                className="text-[#14532d] hover:text-[#0f4a24] font-semibold"
                            >
                                + Add another Exclusion
                            </button>
                        </Section>

                        {/* SAVE BUTTONS */}
                        <div className="flex gap-3 mt-6 bg-white p-6 rounded-lg shadow-sm">
                            <button
                                type="submit"
                                className="bg-[#14532d] px-6 py-2 rounded text-white hover:bg-[#0f4a24] transition font-semibold"
                                disabled={saving}
                            >
                                {saving ? "UPDATING..." : "UPDATE PACKAGE"}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div >
    );
};

export default HolidayPackageEdit;

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Edit2, Trash2, Plus, Search } from "lucide-react";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";

const HolidayPackageManage = () => {
  const [packages, setPackages] = useState([]);
  const [filteredPackages, setFilteredPackages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [searchTerm, setSearchTerm] = useState("");

  const navigate = useNavigate();
  const API_BASE_URL = "/api";

  useEffect(() => {
    fetchPackages();
  }, []);

  const fetchPackages = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/packages/`);
      setPackages(response.data);
      setFilteredPackages(response.data);
      setError("");
    } catch (err) {
      console.error("Error fetching packages:", err);
      setError(`Failed to load packages: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const filtered = packages.filter(pkg =>
      pkg.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      pkg.category?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      pkg.description?.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredPackages(filtered);
  }, [searchTerm, packages]);

  const handleEdit = (pkg) => {
    navigate(`/admin/packages/edit/${pkg.id}`);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this package?")) {
      try {
        setLoading(true);
        await axios.delete(`${API_BASE_URL}/packages/${id}/`);
        setMessage("Package deleted successfully!");
        fetchPackages();
      } catch (err) {
        console.error("Error deleting package:", err);
        setError("Failed to delete package. Please try again.");
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="flex bg-gray-100 min-h-screen">
      <AdminSidebar />
      <div className="flex-1">
        <AdminTopbar />
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-xl font-semibold text-gray-800">Manage Holiday Packages</h1>
            <div className="flex gap-3">
              <button
                onClick={fetchPackages}
                className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700 transition"
              >
                Refresh List
              </button>
              <button
                onClick={() => navigate("/admin/packages/add")}
                className="flex items-center gap-2 bg-[#14532d] text-white px-4 py-2 rounded hover:bg-[#0f4a24] transition"
              >
                <Plus size={16} />
                Add New Package
              </button>
            </div>
          </div>

          {/* Search Bar */}
          <div className="mb-6">
            <div className="relative max-w-md">
              <Search size={20} className="absolute left-3 top-2.5 text-gray-400" />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search packages by name, category, or description..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#14532d]"
              />
            </div>
          </div>

          {message && (
            <div className="mb-4 p-4 bg-green-50 border border-green-200 text-green-700 rounded">
              {message}
            </div>
          )}
          {error && (
            <div className="mb-4 p-4 bg-red-50 border border-red-200 text-red-700 rounded">
              {error}
            </div>
          )}

          <div className="bg-white rounded-lg shadow-md overflow-hidden">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50 border-b">
                  <tr>
                    <th className="text-left py-3 px-4 font-semibold text-gray-700">Name</th>
                    <th className="text-left py-3 px-4 font-semibold text-gray-700">Category</th>
                    <th className="text-left py-3 px-4 font-semibold text-gray-700">Price</th>
                    <th className="text-left py-3 px-4 font-semibold text-gray-700">Duration</th>
                    <th className="text-left py-3 px-4 font-semibold text-gray-700">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredPackages.length === 0 ? (
                    <tr>
                      <td colSpan="5" className="text-center py-8 text-gray-500">
                        {searchTerm ? "No packages match your search" : "No packages found. Add your first package!"}
                      </td>
                    </tr>
                  ) : (
                    filteredPackages.map((pkg) => (
                      <tr key={pkg.id} className="border-b hover:bg-gray-50">
                        <td className="py-3 px-4 font-medium">{pkg.title || pkg.name}</td>
                        <td className="py-3 px-4">{pkg.category}</td>
                        <td className="py-3 px-4">${pkg.price || pkg.Offer_price}</td>
                        <td className="py-3 px-4">{pkg.days || pkg.duration} days</td>
                        <td className="py-3 px-4">
                          <div className="flex gap-2">
                            <button
                              onClick={() => handleEdit(pkg)}
                              className="flex items-center gap-1 bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700 transition"
                            >
                              <Edit2 size={12} />
                              Edit
                            </button>
                            <button
                              onClick={() => handleDelete(pkg.id)}
                              className="flex items-center gap-1 bg-red-600 text-white px-3 py-1 rounded text-sm hover:bg-red-700 transition"
                            >
                              <Trash2 size={12} />
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HolidayPackageManage;

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";

const ItineraryMasterEdit = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    const [form, setForm] = useState({
        name: "",
        title: "",
        description: "",
        image: null,
    });
    const [existingImage, setExistingImage] = useState(null);
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [message, setMessage] = useState("");
    const [error, setError] = useState("");

    const API_BASE_URL = "/api";

    useEffect(() => {
        fetchItinerary();
    }, [id]);

    const fetchItinerary = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`${API_BASE_URL}/itinerary-masters/${id}/`);
            const data = response.data;
            setForm({
                name: data.name,
                title: data.title,
                description: data.description,
                image: null, // Don't pre-fill file input
            });
            setExistingImage(data.image); // URL of existing image
            setError("");
        } catch (err) {
            console.error("Error fetching itinerary:", err);
            setError("Failed to load itinerary details.");
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        if (e.target.name === "image") {
            setForm({ ...form, image: e.target.files[0] });
        } else {
            setForm({ ...form, [e.target.name]: e.target.value });
        }
        setMessage("");
        setError("");
    };

    const handleSubmit = async (e) => {
        if (e) e.preventDefault();
        setSaving(true);
        setMessage("");
        setError("");

        const formData = new FormData();
        formData.append("name", form.name);
        formData.append("title", form.title);
        formData.append("description", form.description);
        if (form.image) {
            formData.append("image", form.image);
        }

        try {
            const response = await axios.put(`${API_BASE_URL}/itinerary-masters/${id}/`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            if (response.status === 200) {
                setMessage("Itinerary Master updated successfully!");
                // Refresh data to show new image if any
                setForm(prev => ({ ...prev, image: null }));
                fetchItinerary();
            }
        } catch (err) {
            console.error("Error updating itinerary master:", err);
            if (err.response?.data) {
                const errorMessages = Object.values(err.response.data).flat();
                setError(errorMessages.join(", "));
            } else {
                setError("Failed to update itinerary master. Please try again.");
            }
        } finally {
            setSaving(false);
        }
    };

    if (loading) {
        return (
            <div className="flex bg-gray-100 min-h-screen">
                <AdminSidebar />
                <div className="flex-1 p-10 flex justify-center items-center">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-700"></div>
                </div>
            </div>
        );
    }

    return (
        <div className="flex bg-gray-100 min-h-screen">
            <AdminSidebar />

            <div className="flex-1">
                <AdminTopbar />

                <div className="p-6">
                    <div className="flex justify-between items-center mb-6">
                        <h1 className="text-xl font-semibold text-gray-800">Edit Itinerary Master</h1>
                        <button
                            onClick={() => navigate('/admin/itinerary-masters')}
                            className="bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300"
                        >
                            Back to List
                        </button>
                    </div>

                    {message && (
                        <div className="mb-4 p-4 bg-green-50 border border-green-200 text-green-700 rounded">
                            {message}
                        </div>
                    )}
                    {error && (
                        <div className="mb-4 p-4 bg-red-50 border border-red-200 text-red-700 rounded">
                            {error}
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="max-w-xl bg-white rounded-lg shadow-md p-6 space-y-4">
                        <div className="flex flex-col gap-2">
                            <label className="text-gray-700 font-medium">Name (Internal ID):</label>
                            <input
                                name="name"
                                value={form.name}
                                onChange={handleChange}
