import React, { useState, useEffect } from "react";
import axios from "axios";
import { ArrowLeft, Save, Trash2 } from "lucide-react";
import { useNavigate, useParams } from "react-router-dom";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";

const UmrahDestinationEdit = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        name: "",
        country: "",
    });
    const [statusMessage, setStatusMessage] = useState({ text: "", type: "" });
    const [loading, setLoading] = useState(true);
    const [isSubmitting, setIsSubmitting] = useState(false);

    useEffect(() => {
        fetchDestination();
    }, [id]);

    const fetchDestination = async () => {
        try {
            const response = await axios.get(`/api/umrah-destinations/${id}/`);
            setFormData({
                name: response.data.name,
                country: response.data.country,
            });
            setLoading(false);
        } catch (error) {
            console.error("Error fetching Umrah destination:", error);
            setStatusMessage({ text: "Failed to fetch destination details", type: "error" });
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e, action = "save") => {
        if (e) e.preventDefault();

        if (!formData.name || !formData.country) {
            setStatusMessage({ text: "Please fill in all required fields.", type: "error" });
            return;
        }

        setIsSubmitting(true);
        setStatusMessage({ text: "", type: "" });

        try {
            await axios.put(`/api/umrah-destinations/${id}/`, formData);

            if (action === "continue") {
                setStatusMessage({ text: "Changes saved successfully!", type: "success" });
                setIsSubmitting(false);
            } else {
                navigate("/admin/umrah-destinations");
            }
        } catch (error) {
            console.error("Error updating Umrah destination:", error);
            setStatusMessage({ text: "Failed to update destination. Please try again.", type: "error" });
            setIsSubmitting(false);
        }
    };

    const handleDelete = async () => {
        if (window.confirm("Are you sure you want to delete this Umrah destination?")) {
            try {
                await axios.delete(`/api/umrah-destinations/${id}/`);
                navigate("/admin/umrah-destinations");
            } catch (error) {
                console.error("Error deleting destination:", error);
                setStatusMessage({ text: "Failed to delete destination.", type: "error" });
            }
        }
    };

    if (loading) {
        return (
            <div className="flex bg-gray-100 min-h-screen">
                <AdminSidebar />
                <div className="flex-1 flex items-center justify-center">
                    <div className="w-12 h-12 border-4 border-green-600 border-t-transparent rounded-full animate-spin"></div>
                </div>
            </div>
        );
    }

    return (
        <div className="flex bg-gray-100 min-h-screen">
            <AdminSidebar />
            <div className="flex-1">
                <AdminTopbar />
                <div className="p-6">
                    <div className="max-w-4xl mx-auto">
                        <div className="flex items-center justify-between mb-8">
                            <div className="flex items-center gap-4">
                                <button
                                    onClick={() => navigate("/admin/umrah-destinations")}
                                    className="p-2 hover:bg-gray-100 rounded-full transition-colors"
                                >
                                    <ArrowLeft size={24} className="text-gray-600" />
                                </button>
                                <h1 className="text-2xl font-bold text-gray-800">Edit Umrah Destination</h1>
                            </div>
                            <button
                                onClick={handleDelete}
                                className="px-4 py-2 bg-red-50 text-red-600 hover:bg-red-100 rounded-lg font-medium flex items-center gap-2 transition-all border border-red-200"
                            >
                                <Trash2 size={18} />
                                Delete
                            </button>
                        </div>

                        {statusMessage.text && (
                            <div
                                className={`mb-6 p-4 rounded-lg flex justify-between items-center ${statusMessage.type === "success" ? "bg-green-100 text-green-800" : "bg-red-100 text-red-800"
                                    }`}
                            >
                                <span>{statusMessage.text}</span>
                                <button onClick={() => setStatusMessage({ text: "", type: "" })}>Γ£ò</button>
                            </div>
                        )}

                        <form className="bg-white rounded-xl shadow-md border border-gray-200 overflow-hidden">
                            <div className="p-8 space-y-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div className="space-y-2">
                                        <label className="text-sm font-semibold text-gray-700 uppercase tracking-wider">
                                            City Name <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleChange}
                                            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none transition-all"
                                            required
                                        />
                                    </div>

                                    <div className="space-y-2">
                                        <label className="text-sm font-semibold text-gray-700 uppercase tracking-wider">
                                            Country <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            name="country"
                                            value={formData.country}
                                            onChange={handleChange}
                                            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none transition-all"
                                            required
                                        />
                                    </div>
                                </div>
                            </div>

                            <div className="p-6 bg-gray-50 border-t flex flex-wrap justify-end gap-3">
                                <button
                                    type="button"
                                    onClick={(e) => handleSubmit(e, "save")}
                                    disabled={isSubmitting}
                                    className="px-6 py-2.5 bg-[#14532d] hover:bg-[#1f7a45] text-white rounded-lg font-semibold flex items-center gap-2 transition-all disabled:opacity-50"
                                >
                                    <Save size={18} />
                                    SAVE
                                </button>
                                <button
                                    type="button"
                                    onClick={(e) => handleSubmit(e, "continue")}
                                    disabled={isSubmitting}
                                    className="px-6 py-2.5 border border-[#14532d] text-[#14532d] hover:bg-green-50 rounded-lg font-semibold transition-all disabled:opacity-50"
                                >
                                    SAVE AND CONTINUE EDITING
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UmrahDestinationEdit;

import React, { useState, useEffect } from "react";
import axios from "axios";
import { Search, Eye, Trash2, Mail, Phone, Calendar } from "lucide-react";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";
import { useNavigate } from "react-router-dom";

const UmrahEnquiryManage = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [filteredEnquiries, setFilteredEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedEnquiry, setSelectedEnquiry] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  const API_BASE_URL = "/api";

  useEffect(() => {
    fetchEnquiries();
  }, []);

  const fetchEnquiries = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/umrah-form/`);
      setEnquiries(response.data);
      setFilteredEnquiries(response.data);
      setError("");
    } catch (err) {
      console.error("Error fetching umrah enquiries:", err);
      setError(`Failed to load umrah enquiries: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const filtered = enquiries.filter(enquiry =>
      enquiry.first_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.last_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.phone?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      enquiry.package_type?.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredEnquiries(filtered);
  }, [searchTerm, enquiries]);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this umrah enquiry?")) {
      try {
        setLoading(true);
        await axios.delete(`${API_BASE_URL}/umrah-form/${id}/`);
        fetchEnquiries();
      } catch (err) {
        console.error("Error deleting umrah enquiry:", err);
        setError("Failed to delete umrah enquiry. Please try again.");
      } finally {
        setLoading(false);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <div className="flex bg-gray-100 min-h-screen">
      <AdminSidebar />
      <div className="flex-1">
        <AdminTopbar />
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-xl font-semibold text-gray-800">Select umrah enquiry to change</h1>
            <div className="flex gap-4">
              <button
                onClick={fetchEnquiries}
                className="bg-gray-100 border border-gray-300 text-gray-600 px-4 py-2 rounded font-medium hover:bg-gray-200 transition text-sm"
              >
                Refresh
              </button>
              <button
                onClick={() => navigate("/admin/umrah-enquiries/add")}
                className="bg-[#14532d] text-white px-4 py-2 rounded font-medium hover:bg-[#0f4a24] transition text-sm uppercase tracking-wider"
              >
                Add Umrah Enquiry
              </button>
            </div>
          </div>

          {/* Search Bar */}
          <div className="mb-6">
            <div className="relative max-w-md">
              <Search size={20} className="absolute left-3 top-2.5 text-gray-400" />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search umrah enquiries by name, email, phone, or package type..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#14532d]"
              />
            </div>
          </div>

          {error && (
            <div className="mb-4 p-4 bg-red-50 border border-red-200 text-red-700 rounded">
              {error}
            </div>
          )}

          <div className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-[#14532d] text-white">
                  <tr>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Name</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Contact</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Package Type</th>
                    <th className="text-left py-4 px-6 font-semibold uppercase text-sm tracking-wider">Travel Date</th>
                    <th className="text-center py-4 px-6 font-semibold uppercase text-sm tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {filteredEnquiries.length === 0 ? (
                    <tr>
                      <td colSpan="5" className="text-center py-10 text-gray-500">
                        {searchTerm ? `No umrah enquiries match "${searchTerm}"` : "No umrah enquiries found."}
                      </td>
                    </tr>
                  ) : (
                    filteredEnquiries.map((enquiry) => (
                      <tr key={enquiry.id} className="hover:bg-gray-50 transition-colors">
                        <td className="py-4 px-6 font-medium text-gray-900 border-r">
                          {enquiry.first_name} {enquiry.last_name}
                        </td>
                        <td className="py-4 px-6 border-r text-gray-600">
                          <div className="space-y-1">
                            <div className="flex items-center gap-2 text-sm">
                              <Mail size={14} className="text-gray-400" />
                              {enquiry.email}
                            </div>
                            <div className="flex items-center gap-2 text-sm">
                              <Phone size={14} className="text-gray-400" />
                              {enquiry.phone}
                            </div>
                          </div>
                        </td>
                        <td className="py-3 px-4">{enquiry.package_type}</td>
                        <td className="py-3 px-4 text-sm">
                          <div className="flex items-center gap-2">
                            <Calendar size={14} className="text-gray-400" />
                            {formatDate(enquiry.travel_date)}
                          </div>
                        </td>
                        <td className="py-3 px-4">
                          <div className="flex gap-2">
                            <button
                              onClick={() => setSelectedEnquiry(enquiry)}
                              className="flex items-center gap-1 bg-blue-600 text-white px-3 py-1 rounded text-sm"
                            >
                              <Eye size={12} />
                              View
                            </button>
                            <button
                              onClick={() => handleDelete(enquiry.id)}
                              className="flex items-center gap-1 bg-red-600 text-white px-3 py-1 rounded text-sm"
                            >
                              <Trash2 size={12} />
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>

          {/* Enquiry Detail Modal */}
          {selectedEnquiry && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[80vh] overflow-y-auto">
                <div className="flex justify-between items-start mb-4">
                  <h2 className="text-xl font-semibold">Umrah Enquiry Details</h2>
                  <button
                    onClick={() => setSelectedEnquiry(null)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    ├ù
                  </button>
                </div>

                <div className="space-y-4">
                  <div>
                    <h3 className="font-semibold text-gray-700">Personal Information</h3>
                    <p><strong>Name:</strong> {selectedEnquiry.first_name} {selectedEnquiry.last_name}</p>
                    <p><strong>Email:</strong> {selectedEnquiry.email}</p>
                    <p><strong>Phone:</strong> {selectedEnquiry.phone}</p>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-700">Umrah Details</h3>
                    <p><strong>Package Type:</strong> {selectedEnquiry.package_type}</p>
                    <p><strong>Travel Date:</strong> {formatDate(selectedEnquiry.travel_date)}</p>
                    <p><strong>Duration:</strong> {selectedEnquiry.duration} days</p>
                    <p><strong>Budget:</strong> ${selectedEnquiry.budget}</p>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-700">Group Information</h3>
                    <p><strong>Adults:</strong> {selectedEnquiry.adults}</p>
                    <p><strong>Children:</strong> {selectedEnquiry.children}</p>
                    <p><strong>Infants:</strong> {selectedEnquiry.infants}</p>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-700">Additional Information</h3>
                    <p><strong>Message:</strong></p>
                    <p className="text-gray-600 whitespace-pre-wrap">{selectedEnquiry.message}</p>
                  </div>

                  <div>
                    <h3 className="font-semibold text-gray-700">Timestamp</h3>
                    <p>Submitted: {formatDate(selectedEnquiry.created_at)}</p>
                  </div>
                </div>

                <div className="mt-6 flex gap-3">
                  <button
                    onClick={() => setSelectedEnquiry(null)}
                    className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
                  >
                    Close
                  </button>
                  <button
                    onClick={() => handleDelete(selectedEnquiry.id)}
                    className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                  >
                    Delete Enquiry
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UmrahEnquiryManage;

import React, { useState, useEffect } from "react";
import AdminSidebar from "../../components/admin/AdminSidebar";
import AdminTopbar from "../../components/admin/AdminTopbar";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const UserEdit = () => {
    const { id } = useParams();
    const [form, setForm] = useState({
        username: "",
        first_name: "",
        last_name: "",
        email: "",
        is_active: true,
        is_staff: false,
        is_superuser: false,
        last_login: "",
        date_joined: "",
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    const API_BASE_URL = "/api";

    useEffect(() => {
        fetchUser();
    }, [id]);

    const fetchUser = async () => {
        try {
            const response = await axios.get(`${API_BASE_URL}/users/${id}/`);
            const data = response.data;
            setForm({
                username: data.username || "",
                first_name: data.first_name || "",
                last_name: data.last_name || "",
                email: data.email || "",
                is_active: data.is_active !== undefined ? data.is_active : true,
                is_staff: data.is_staff || false,
                is_superuser: data.is_superuser || false,
                last_login: data.last_login || "",
                date_joined: data.date_joined || "",
            });
            setLoading(false);
        } catch (err) {
            console.error("Error fetching user:", err);
            setError("Failed to load user data.");
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setForm({
            ...form,
            [name]: type === 'checkbox' ? checked : value
        });
    };

    const handleSave = async (redirectType) => {
        setError("");
        try {
            await axios.put(`${API_BASE_URL}/users/${id}/`, form);
            alert("User updated successfully!");
            if (redirectType === 'save') {
                navigate("/admin/users");
            }
        } catch (err) {
            console.error(err);
            setError("Failed to update user. Please try again.");
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return "-";
        const date = new Date(dateString);
        return date.toLocaleDateString() + " " + date.toLocaleTimeString();
    };

    if (loading) return (
        <div className="flex bg-gray-100 min-h-screen">
            <AdminSidebar />
            <div className="flex-1">
                <AdminTopbar />
                <div className="p-6 flex justify-center items-center h-[60vh]">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-[#14532d]"></div>
                </div>
            </div>
        </div>
    );

    return (
        <div className="flex bg-gray-100 min-h-screen">
            <AdminSidebar />
            <div className="flex-1">
                <AdminTopbar />
                <div className="p-6">
                    <div className="max-w-5xl mx-auto bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
                        {/* Header */}
                        <div className="p-6 border-b border-gray-100 flex justify-between items-start bg-white">
                            <div>
                                <h1 className="text-sm text-gray-500 uppercase tracking-wider font-semibold">Change user</h1>
                                <h2 className="text-2xl font-bold text-gray-800 mt-1">{form.username}</h2>
                            </div>
                            <button className="bg-gray-100 hover:bg-gray-200 text-gray-600 px-4 py-1 rounded text-xs font-bold uppercase tracking-tight transition-colors">
                                History
                            </button>
                        </div>

                        {error && (
                            <div className="m-6 p-4 bg-red-50 border-l-4 border-red-500 text-red-700 text-sm">
                                {error}
                            </div>
                        )}

                        <div className="p-6 space-y-8">
                            {/* Basic Info */}
                            <div className="space-y-6">
                                <div className="grid grid-cols-1 md:grid-cols-3 items-center gap-4">
                                    <label className="text-sm font-bold text-gray-700">Username:</label>
                                    <div className="md:col-span-2">
                                        <input
                                            name="username"
                                            value={form.username}
                                            onChange={handleChange}
                                            className="w-full max-w-md border border-gray-300 px-3 py-2 rounded focus:ring-1 focus:ring-[#14532d] focus:border-[#14532d] outline-none text-sm transition-all"
                                        />
                                        <p className="text-[11px] text-gray-500 mt-1.5 leading-relaxed">
                                            Required. 150 characters or fewer. Letters, digits and @/./+/-/_ only.
                                        </p>
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-3 items-start gap-4 pt-4 border-t border-gray-50">
                                    <label className="text-sm font-bold text-gray-700 mt-1">Password:</label>
                                    <div className="md:col-span-2">
                                        <p className="text-xs text-gray-600 bg-gray-50 p-3 rounded border border-gray-100">
                                            <span className="font-mono font-bold">algorithm:</span> pbkdf2_sha256 <span className="font-mono font-bold ml-2">iterations:</span> 600000 <span className="font-mono font-bold ml-2">salt:</span> ******** <span className="font-mono font-bold ml-2">hash:</span> ********
                                            <br />
                                            <span className="inline-block mt-2 text-gray-500 italic">Raw passwords are not stored. You can change the password using <span className="text-blue-600 cursor-pointer hover:underline">this form</span>.</span>
                                        </p>
                                    </div>
                                </div>
                            </div>

                            {/* Personal Info Section */}
                            <div className="rounded border border-gray-200 overflow-hidden">
                                <div className="bg-[#14532d] px-4 py-2 text-white text-xs font-bold uppercase tracking-wider">
                                    Personal Info
                                </div>
                                <div className="p-6 space-y-6 bg-white">
                                    <div className="grid grid-cols-1 md:grid-cols-3 items-center gap-4">
                                        <label className="text-sm font-bold text-gray-700">First name:</label>
                                        <input
                                            name="first_name"
                                            value={form.first_name}
                                            onChange={handleChange}
                                            className="md:col-span-2 w-full max-w-md border border-gray-300 px-3 py-2 rounded focus:ring-1 focus:ring-[#14532d] outline-none text-sm"
                                        />
                                    </div>
                                    <div className="grid grid-cols-1 md:grid-cols-3 items-center gap-4 pt-4 border-t border-gray-50">
                                        <label className="text-sm font-bold text-gray-700">Last name:</label>
                                        <input
                                            name="last_name"
                                            value={form.last_name}
                                            onChange={handleChange}
                                            className="md:col-span-2 w-full max-w-md border border-gray-300 px-3 py-2 rounded focus:ring-1 focus:ring-[#14532d] outline-none text-sm"
                                        />
                                    </div>
                                    <div className="grid grid-cols-1 md:grid-cols-3 items-center gap-4 pt-4 border-t border-gray-50">
                                        <label className="text-sm font-bold text-gray-700">Email address:</label>
                                        <input
                                            name="email"
                                            type="email"
                                            value={form.email}
                                            onChange={handleChange}
                                            className="md:col-span-2 w-full max-w-md border border-gray-300 px-3 py-2 rounded focus:ring-1 focus:ring-[#14532d] outline-none text-sm"
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Permissions Section */}
                            <div className="rounded border border-gray-200 overflow-hidden">
                                <div className="bg-[#14532d] px-4 py-2 text-white text-xs font-bold uppercase tracking-wider">
                                    Permissions
                                </div>
                                <div className="p-6 space-y-6 bg-white">
                                    <div className="flex items-start gap-4">
                                        <input
                                            type="checkbox"
                                            name="is_active"
                                            checked={form.is_active}
                                            onChange={handleChange}
                                            className="mt-1 h-4 w-4 rounded border-gray-300 text-[#14532d] focus:ring-[#14532d]"
                                        />
                                        <div>
                                            <label className="text-sm font-bold text-gray-700">Active</label>
                                            <p className="text-[11px] text-gray-500">Designates whether this user should be treated as active. Unselect this instead of deleting accounts.</p>
                                        </div>
                                    </div>
                                    <div className="flex items-start gap-4 pt-4 border-t border-gray-50">
                                        <input
                                            type="checkbox"
                                            name="is_staff"
                                            checked={form.is_staff}
                                            onChange={handleChange}
                                            className="mt-1 h-4 w-4 rounded border-gray-300 text-[#14532d] focus:ring-[#14532d]"
                                        />
                                        <div>
                                            <label className="text-sm font-bold text-gray-700">Staff status</label>
                                            <p className="text-[11px] text-gray-500">Designates whether the user can log into this admin site.</p>
                                        </div>
                                    </div>
                                    <div className="flex items-start gap-4 pt-4 border-t border-gray-50">
                                        <input
                                            type="checkbox"
                                            name="is_superuser"
                                            checked={form.is_superuser}
                                            onChange={handleChange}
                                            className="mt-1 h-4 w-4 rounded border-gray-300 text-[#14532d] focus:ring-[#14532d]"
                                        />
                                        <div>
                                            <label className="text-sm font-bold text-gray-700">Superuser status</label>
                                            <p className="text-[11px] text-gray-500">Designates that this user has all permissions without explicitly assigning them.</p>
                                        </div>
                                    </div>

                                    {/* Groups / Permissions Box Selectors UI (Conceptual) */}
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8 pt-6 border-t border-gray-50">
                                        <div>
                                            <label className="text-sm font-bold text-gray-700 block mb-3 uppercase tracking-tighter">Groups:</label>
                                            <div className="border border-gray-200 rounded min-h-[150px] bg-gray-50 p-4 text-center flex flex-col justify-center items-center">
                                                <p className="text-xs text-gray-400 italic">No groups available</p>
                                            </div>
                                        </div>
                                        <div>
                                            <label className="text-sm font-bold text-gray-700 block mb-3 uppercase tracking-tighter invisible">Chosen:</label>
                                            <div className="border border-gray-200 rounded min-h-[150px] bg-gray-50 p-4 text-center flex flex-col justify-center items-center">
                                                <p className="text-xs text-gray-400 italic">No groups selected</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Important Dates Section */}
                            <div className="rounded border border-gray-200 overflow-hidden">
                                <div className="bg-[#14532d] px-4 py-2 text-white text-xs font-bold uppercase tracking-wider">
                                    Important Dates
                                </div>
                                <div className="p-6 space-y-6 bg-white">
                                    <div className="grid grid-cols-1 md:grid-cols-3 items-center gap-4">
                                        <label className="text-sm font-bold text-gray-700 uppercase tracking-tighter">Last login:</label>
                                        <div className="md:col-span-2 text-sm text-gray-600 font-medium">
                                            {formatDate(form.last_login)}
                                        </div>
                                    </div>
                                    <div className="grid grid-cols-1 md:grid-cols-3 items-center gap-4 pt-4 border-t border-gray-50">
                                        <label className="text-sm font-bold text-gray-700 uppercase tracking-tighter">Date joined:</label>
                                        <div className="md:col-span-2 text-sm text-gray-600 font-medium">
                                            {formatDate(form.date_joined)}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>

                        {/* Footer Actions */}
                        <div className="bg-gray-50 border-t border-gray-200 p-4 flex flex-col md:flex-row justify-between items-center gap-4">
                            <div className="flex gap-3 order-2 md:order-1 w-full md:w-auto">
                                <button
                                    onClick={() => handleSave('save')}
                                    className="flex-1 md:flex-none bg-[#14532d] hover:bg-[#0f4a24] text-white px-8 py-2.5 rounded text-xs font-bold uppercase tracking-widest shadow-sm transition-all"
                                >
                                    Save
                                </button>
                                <button className="flex-1 md:flex-none bg-[#457b9d] hover:bg-[#345d7a] text-white px-4 py-2.5 rounded text-xs font-bold uppercase tracking-widest shadow-sm transition-all">
                                    Save and add another
                                </button>
                                <button className="flex-1 md:flex-none bg-[#457b9d] hover:bg-[#345d7a] text-white px-4 py-2.5 rounded text-xs font-bold uppercase tracking-widest shadow-sm transition-all">
                                    Save and continue editing
                                </button>
                            </div>
                            <button
                                onClick={() => {
                                    if (window.confirm("Are you sure you want to delete this user? This action cannot be undone.")) {
                                        axios.delete(`${API_BASE_URL}/users/${id}/`)
                                            .then(() => { navigate("/admin/users"); })
                                            .catch(() => alert("Error deleting user"));
                                    }
                                }}
                                className="order-1 md:order-2 w-full md:w-auto bg-red-600 hover:bg-red-700 text-white px-8 py-2.5 rounded text-xs font-bold uppercase tracking-widest shadow-sm transition-all"
                            >
                                Delete
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UserEdit;
